import React, { useState, useEffect } from 'react'; // Added useEffect here
import FilterPanel from './components/FilterPanel';
import DoctorList from './components/DoctorList';
import './App.css'; // Importing CSS for styling
import SearchBar from './components/SearchBar';

const App = () => {
  const [doctors, setDoctors] = useState([]); // State for doctors
  const [filters, setFilters] = useState({
    consultationType: '',
    specialties: [],
    sortOption: '',
  });

  // Fetch doctor data (API request example)
  useEffect(() => {
    fetch('https://srijandubey.github.io/campus-api-mock/SRM-C1-25.json')
      .then((response) => response.json())
      .then((data) => setDoctors(data));
  }, []); // Dependency array is empty, so it will run only once on mount

  // onFilterChange function to update filters
  const handleFilterChange = (newFilters) => {
    setFilters(newFilters);
  };

  // Filter doctors based on the selected filters
  const filteredDoctors = doctors
    .filter((doctor) => {
      const { consultationType, specialties } = filters;
      let isMatch = true;

      // Check consultation type filter
      if (consultationType && !doctor[consultationType.toLowerCase()]) {
        isMatch = false;
      }

      // Check specialties filter
      if (specialties.length > 0 && !specialties.some((specialty) =>
        doctor.specialities?.map((s) => s.name).includes(specialty)
      )) {
        isMatch = false;
      }

      return isMatch;
    })
    .sort((a, b) => {
      const { sortOption } = filters;
      if (sortOption === 'fees-asc') {
        return a.fees - b.fees; // Sort by fees in ascending order
      }
      if (sortOption === 'experience-desc') {
        return b.experience - a.experience; // Sort by experience in descending order
      }
      return 0; // No sorting if no option is selected
    });

  return (
    <div>
      <SearchBar doctors={doctors} onSearch={(query) => {
        const filtered = doctors.filter((doctor) =>
          doctor.name.toLowerCase().includes(query.toLowerCase())
        );
        setDoctors(filtered); // Update the doctor list based on search query
      }
      } />
      <FilterPanel doctors={doctors} onFilterChange={handleFilterChange} />
      <DoctorList doctors={filteredDoctors} />
    </div>
  );
};

export default App;
