import React, { useEffect, useState } from 'react';

const FilterPanel = ({ doctors, selectedConsultationType, setSelectedConsultationType, selectedSpecialties, setSelectedSpecialties, handleSortChange }) => {
  const [allSpecialties, setAllSpecialties] = useState([]);

  // Generate specialties dynamically from doctor data
  useEffect(() => {
    const specialtiesSet = new Set();
    doctors.forEach((doctor) => {
      doctor.specialities.forEach((specialty) => {
        specialtiesSet.add(specialty.name); // Make sure we add the 'name' property of the specialty object
      });
    });
    setAllSpecialties([...specialtiesSet]); // Convert set to array and update state
  }, [doctors]);

  const handleConsultationTypeChange = (event) => {
    setSelectedConsultationType(event.target.value);
  };

  const handleSpecialtyChange = (specialty) => {
    // Check if specialty is already selected, and toggle its presence in the selectedSpecialties array
    const updatedSpecialties = selectedSpecialties.includes(specialty)
      ? selectedSpecialties.filter((item) => item !== specialty)
      : [...selectedSpecialties, specialty];
    setSelectedSpecialties(updatedSpecialties);
  };

  return (
    <div className="filter-panel">
      <div>
        <h3>Consultation Type</h3>
        <label>
          <input
            type="radio"
            name="consultation"
            value="online"
            checked={selectedConsultationType === 'online'}
            onChange={handleConsultationTypeChange}
          />
          Video Consult
        </label>
        <label>
          <input
            type="radio"
            name="consultation"
            value="clinic"
            checked={selectedConsultationType === 'clinic'}
            onChange={handleConsultationTypeChange}
          />
          In Clinic
        </label>
      </div>

      <div>
        <h3>Specialties</h3>
        {allSpecialties.map((specialty) => (
          <label key={specialty}>
            <input
              type="checkbox"
              value={specialty}
              checked={selectedSpecialties.includes(specialty)}
              onChange={() => handleSpecialtyChange(specialty)} // Pass the specialty name to handle change
            />
            {specialty}
          </label>
        ))}
      </div>

      <div>
        <h3>Sort By</h3>
        <button onClick={() => handleSortChange('fees')}>Sort by Fees</button>
        <button onClick={() => handleSortChange('experience')}>Sort by Experience</button>
      </div>
    </div>
  );
};

