import React, { useState } from 'react';
import FilterPanel from './components/FilterPanel';
import DoctorList from './components/DoctorList';

const App = () => {
  const [doctors, setDoctors] = useState([]); // State for doctors
  const [filters, setFilters] = useState({
    consultationType: '',
    specialties: [],
    sortOption: '',
  });

  // Fetch doctor data (API request example)
  useEffect(() => {
    fetch('https://srijandubey.github.io/campus-api-mock/SRM-C1-25.json')
      .then((response) => response.json())
      .then((data) => setDoctors(data));
  }, []);

  // onFilterChange function to update filters
  const handleFilterChange = (newFilters) => {
    setFilters(newFilters);
  };

  // Filter doctors based on the selected filters
  const filteredDoctors = doctors.filter((doctor) => {
    const { consultationType, specialties, sortOption } = filters;
    let isMatch = true;

    // Check consultation type filter
    if (consultationType && !doctor[consultationType.toLowerCase()]) {
      isMatch = false;
    }

    // Check specialties filter
    if (specialties.length > 0 && !specialties.some((specialty) =>
      doctor.specialities?.map((s) => s.name).includes(specialty)
    )) {
      isMatch = false;
    }

    // You can also implement sorting logic here if needed

    return isMatch;
  });

  return (
    <div>
      <FilterPanel doctors={doctors} onFilterChange={handleFilterChange} />
      <DoctorList doctors={filteredDoctors} />
    </div>
  );
};

export default App;
