// src/components/FilterPanel.js
import React, { useState } from "react";

const FilterPanel = ({ setDoctors, doctors }) => {
  const [consultationType, setConsultationType] = useState("");
  const [specialties, setSpecialties] = useState([]);
  const [sortOption, setSortOption] = useState("");

  const handleConsultationTypeChange = (e) => {
    setConsultationType(e.target.value);
  };

  const handleSpecialtyChange = (e) => {
    const { value, checked } = e.target;
    setSpecialties((prev) =>
      checked ? [...prev, value] : prev.filter((item) => item !== value)
    );
  };

  const handleSortChange = (e) => {
    setSortOption(e.target.value);
  };

  const applyFilters = () => {
    let filteredDoctors = [...doctors];

    // Filter by consultation type
    if (consultationType) {
      filteredDoctors = filteredDoctors.filter(
        (doctor) => doctor.consultationType === consultationType
      );
    }

    // Filter by specialties
    if (specialties.length > 0) {
      filteredDoctors = filteredDoctors.filter((doctor) =>
        specialties.every((specialty) =>
          doctor.specialties.includes(specialty)
        )
      );
    }

    // Sorting
    if (sortOption === "fees") {
      filteredDoctors = filteredDoctors.sort((a, b) => a.fee - b.fee);
    } else if (sortOption === "experience") {
      filteredDoctors = filteredDoctors.sort(
        (a, b) => b.experience - a.experience
      );
    }

    setDoctors(filteredDoctors);
  };

  return (
    <div>
      {/* Consultation Type */}
      <div>
        <label>
          <input
            type="radio"
            name="consultation"
            value="Video Consult"
            onChange={handleConsultationTypeChange}
            checked={consultationType === "Video Consult"}
          />
          Video Consult
        </label>
        <label>
          <input
            type="radio"
            name="consultation"
            value="In Clinic"
            onChange={handleConsultationTypeChange}
            checked={consultationType === "In Clinic"}
          />
          In Clinic
        </label>
      </div>

      {/* Specialties */}
      <div>
        <h4>Specialties</h4>
        <label>
          <input
            type="checkbox"
            value="Dentist"
            onChange={handleSpecialtyChange}
          />
          Dentist
        </label>
        {/* Add other specialties here */}
      </div>

      {/* Sort */}
      <div>
        <select onChange={handleSortChange} value={sortOption}>
          <option value="">Sort by</option>
          <option value="fees">Fees (Low to High)</option>
          <option value="experience">Experience (High to Low)</option>
        </select>
      </div>

      <button onClick={applyFilters}>Apply Filters</button>
    </div>
  );
};

export default FilterPanel;
