// src/components/SearchBar.js
import React, { useState, useEffect } from "react";
import { fetchDoctors } from "../services/api"; // Make sure to import the fetchDoctors function

const SearchBar = ({ setDoctors }) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [suggestions, setSuggestions] = useState([]);
  
  useEffect(() => {
    const fetchData = async () => {
      const doctors = await fetchDoctors();
      setDoctors(doctors);  // Pass the data to the parent component
    };
    fetchData();
  }, [setDoctors]);

  const handleSearch = async (e) => {
    setSearchTerm(e.target.value);
    const doctors = await fetchDoctors();
    const filtered = doctors.filter((doctor) =>
      doctor.name.toLowerCase().includes(e.target.value.toLowerCase())
    );
    setSuggestions(filtered.slice(0, 3)); // Show top 3 matches
  };

  const handleSelectSuggestion = (name) => {
    setSearchTerm(name);
    setSuggestions([]);
    // Filter doctors based on selected name
    const filteredDoctors = doctors.filter((doctor) => doctor.name === name);
    setDoctors(filteredDoctors);
  };

  return (
    <div>
      <input
        type="text"
        value={searchTerm}
        onChange={handleSearch}
        placeholder="Search for a doctor..."
        data-testid="autocomplete-input"
      />
      {suggestions.length > 0 && (
        <ul>
          {suggestions.map((doctor) => (
            <li
              key={doctor.id}
              onClick={() => handleSelectSuggestion(doctor.name)}
              data-testid="suggestion-item"
            >
              {doctor.name}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default SearchBar;
