import React, { useState, useEffect } from 'react';
import { fetchDoctors } from './services/api';
import SearchBar from './components/SearchBar';
import DoctorList from './components/DoctorList';
import FilterPanel from './components/FilterPanel';

const App = () => {
  const [doctors, setDoctors] = useState([]);
  const [filteredDoctors, setFilteredDoctors] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [filters, setFilters] = useState({
    consultationType: '',
    specialties: [],
    sortBy: '',
  });

  useEffect(() => {
    let filtered = doctors;

    // Apply consultation type filter
    if (filters.consultationType) {
      filtered = filtered.filter(
        (doctor) => doctor.consultationType === filters.consultationType
      );
    }

    // Apply specialties filter
    if (filters.specialties.length > 0) {
      filtered = filtered.filter((doctor) =>
        filters.specialties.some((specialty) =>
          doctor.specialties.includes(specialty)
        )
      );
    }

    // Apply sort
    if (filters.sortBy === 'fees') {
      filtered = filtered.sort((a, b) => a.fee - b.fee); // Sort by fee (low to high)
    } else if (filters.sortBy === 'experience') {
      filtered = filtered.sort((a, b) => b.experience - a.experience); // Sort by experience (high to low)
    }

    setFilteredDoctors(filtered);
  }, [filters, doctors]);

  const handleSearch = (query) => {
    setSearchQuery(query);
    if (query === '') {
      setFilteredDoctors(doctors);
    } else {
      const filtered = doctors.filter((doctor) =>
        doctor.name.toLowerCase().includes(query.toLowerCase())
      );
      setFilteredDoctors(filtered);
    }
  };

  const handleFilter = (filteredList) => {
    setFilteredDoctors(filteredList);
  };

  return (
    <div>
      <SearchBar doctors={doctors} onSearch={handleSearch} />
      <FilterPanel doctors={doctors} onFilter={handleFilter} setFilters={setFilters} />
      <DoctorList doctors={filteredDoctors} />
    </div>
  );
};

export default App;
