// src/App.js
import React, { useState, useEffect } from 'react';
import { fetchDoctors } from './services/api';
import SearchBar from './components/SearchBar';
import DoctorList from './components/DoctorList';
import FilterPanel from './components/FilterPanel';

const App = () => {
  const [doctors, setDoctors] = useState([]);
  const [filteredDoctors, setFilteredDoctors] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    // Fetch doctors from API
    fetchDoctors().then((data) => {
      setDoctors(data);
      setFilteredDoctors(data); // Initially, show all doctors
    });
  }, []);

  const handleSearch = (query) => {
    setSearchQuery(query); // Update search query
    if (query === '') {
      setFilteredDoctors(doctors); // Reset the list to show all doctors if query is empty
    } else {
      const filtered = doctors.filter((doctor) =>
        doctor.name.toLowerCase().includes(query.toLowerCase()) // Filter by name
      );
      setFilteredDoctors(filtered);
    }
  };

  return (
    <div>
      <SearchBar doctors={doctors} onSearch={handleSearch} /> {/* Pass the handleSearch function */}
      <FilterPanel doctors={doctors} onFilter={handleFilter} />
      <DoctorList doctors={filteredDoctors} />
    </div>
  );
};

export default App;
