import React, { useState, useEffect } from "react";
import { useLocation, useHistory } from "react-router-dom";
import { fetchDoctors } from "./services/api";

const App = () => {
  const location = useLocation();
  const history = useHistory();

  // State variables to store doctors data and filters
  const [doctors, setDoctors] = useState([]);
  const [filteredDoctors, setFilteredDoctors] = useState([]);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState(""); // Search term
  const [consultationType, setConsultationType] = useState(""); // Consultation type
  const [specialties, setSpecialties] = useState([]); // Specialties filter
  const [sort, setSort] = useState(""); // Sort filter (fees/experience)

  // Fetch doctors on mount
  useEffect(() => {
    const getDoctors = async () => {
      try {
        const doctorData = await fetchDoctors();
        setDoctors(doctorData);
        setFilteredDoctors(doctorData); // Initialize filteredDoctors with all doctors
      } catch (err) {
        setError("Failed to load doctors");
        console.error(err);
      }
    };

    getDoctors();
  }, []);

  // Sync filters with URL params
  useEffect(() => {
    const queryParams = new URLSearchParams(location.search);
    setSearchTerm(queryParams.get("search") || "");
    setConsultationType(queryParams.get("consultationType") || "");
    setSpecialties(queryParams.getAll("specialties") || []);
    setSort(queryParams.get("sort") || "");
  }, [location.search]);

  // Update URL params based on filter state
  useEffect(() => {
    const queryParams = new URLSearchParams();

    if (searchTerm) queryParams.set("search", searchTerm);
    if (consultationType) queryParams.set("consultationType", consultationType);
    specialties.forEach((specialty) => queryParams.append("specialties", specialty));
    if (sort) queryParams.set("sort", sort);

    history.push({ search: queryParams.toString() });
  }, [searchTerm, consultationType, specialties, sort, history]);

  // Handle search term change and filter doctors based on name
  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
    const filtered = doctors.filter((doctor) =>
      doctor.name.toLowerCase().includes(e.target.value.toLowerCase())
    );
    setFilteredDoctors(filtered);
  };

  // Handle consultation type filter change (Radio Button)
  const handleConsultationTypeChange = (e) => {
    setConsultationType(e.target.value);
    filterDoctors(e.target.value, specialties, sort);
  };

  // Handle specialty filter change (Checkboxes)
  const handleSpecialtyChange = (e) => {
    const specialty = e.target.value;
    setSpecialties((prevSpecialties) =>
      prevSpecialties.includes(specialty)
        ? prevSpecialties.filter((item) => item !== specialty)
        : [...prevSpecialties, specialty]
    );
  };

  // Handle sort change (Radio Button)
  const handleSortChange = (e) => {
    setSort(e.target.value);
    filterDoctors(consultationType, specialties, e.target.value);
  };

  // Combine all filters and update filteredDoctors based on current filters
  const filterDoctors = (consultationType, specialties, sort) => {
    let updatedDoctors = doctors;

    if (searchTerm) {
      updatedDoctors = updatedDoctors.filter((doctor) =>
        doctor.name.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    if (consultationType) {
      updatedDoctors = updatedDoctors.filter(
        (doctor) => doctor.consultationType === consultationType
      );
    }

    if (specialties.length > 0) {
      updatedDoctors = updatedDoctors.filter((doctor) =>
        doctor.specialty.some((specialty) => specialties.includes(specialty))
      );
    }

    if (sort) {
      updatedDoctors = updatedDoctors.sort((a, b) => {
        if (sort === "fees") {
          return a.fee - b.fee; // Ascending order by fee
        } else if (sort === "experience") {
          return b.experience - a.experience; // Descending order by experience
        }
        return 0;
      });
    }

    setFilteredDoctors(updatedDoctors);
  };

  return (
    <div>
      <h1>Doctor Listing</h1>

      {/* Search Bar */}
      <input
        type="text"
        value={searchTerm}
        onChange={handleSearch}
        placeholder="Search for a doctor"
        data-testid="autocomplete-input"
      />

      {/* Error message */}
      {error && <p>{error}</p>}

      {/* Consultation Type Filter (Radio Buttons) */}
      <div data-testid="filter-header-moc">
        <label>
          <input
            type="radio"
            name="consultationType"
            value="Video Consult"
            checked={consultationType === "Video Consult"}
            onChange={handleConsultationTypeChange}
            data-testid="filter-video-consult"
          />
          Video Consult
        </label>
        <label>
          <input
            type="radio"
            name="consultationType"
            value="In Clinic"
            checked={consultationType === "In Clinic"}
            onChange={handleConsultationTypeChange}
            data-testid="filter-in-clinic"
          />
          In Clinic
        </label>
      </div>

      {/* Specialty Filter (Checkboxes) */}
      <div data-testid="filter-header-speciality">
        {["General Physician", "Dentist", "Dermatologist", "Paediatrician"].map(
          (specialty) => (
            <label key={specialty}>
              <input
                type="checkbox"
                value={specialty}
                checked={specialties.includes(specialty)}
                onChange={handleSpecialtyChange}
                data-testid={`filter-specialty-${specialty.replace(" ", "-")}`}
              />
              {specialty}
            </label>
          )
        )}
      </div>

      {/* Sort Filter (Radio Buttons) */}
      <div data-testid="filter-header-sort">
        <label>
          <input
            type="radio"
            name="sort"
            value="fees"
            checked={sort === "fees"}
            onChange={handleSortChange}
            data-testid="sort-fees"
          />
          Sort by Fees
        </label>
        <label>
          <input
            type="radio"
            name="sort"
            value="experience"
            checked={sort === "experience"}
            onChange={handleSortChange}
            data-testid="sort-experience"
          />
          Sort by Experience
        </label>
      </div>

      {/* Render doctor cards */}
      <div>
        {filteredDoctors.length > 0 ? (
          filteredDoctors.map((doctor) => (
            <div key={doctor.id} data-testid="doctor-card">
              <h2 data-testid="doctor-name">{doctor.name}</h2>
              <p data-testid="doctor-specialty">{doctor.specialty}</p>
              <p data-testid="doctor-experience">
                Experience: {doctor.experience} years
              </p>
              <p data-testid="doctor-fee">Fee: â‚¹{doctor.fee}</p>
            </div>
          ))
        ) : (
          <p>No doctors found</p>
        )}
      </div>
    </div>
  );
};

export default App;
